import XCTest
@testable import SwiftHomework_ZWC

class HMAppstoreTestCase: XCTestCase {

    var appStroe: HMAppstore = HMAppstore()
    
    override func setUpWithError() throws {
        let data = jsonString.data(using: .utf8)
        guard let data = data else { return }
        do {
            let decodedApplication = try JSONDecoder().decode(HMApplicationRequestResult.self, from: data)
            appStroe.applicationListRawValue = decodedApplication.results
        } catch let error {
            print("Error decoding: ", error)
            XCTFail("\(error)")
        }
    }

    override func tearDownWithError() throws {
        
    }
    
    func testCollect() {
        guard appStroe.applicationList.count > 0 else{
            return
        }
        let firstApp = appStroe.applicationList[0]
        appStroe.collect(application: firstApp)
        XCTAssert(appStroe.applicationList[0].isCollected)
    }
    
    func testApplicationIndex() {
        guard appStroe.applicationList.count > 0 else{
            return
        }
        let firstApp = appStroe.applicationList[0]
        let index = appStroe.getApplicationIndex(app: firstApp)
        XCTAssert(index == 0)
    }
    
    func testFetchNextData() {
        guard appStroe.applicationListRawValue.count > 0 else{
            return
        }
        appStroe.fetchNextPageData()
        XCTAssert(appStroe.applicationList.count == 20)
        XCTAssert(appStroe.isHaveMoreData == false)
    }
    
    func textInitializeData() {
        guard appStroe.applicationListRawValue.count > 0 else{
            return
        }
        appStroe.applicationList.removeAll()
        appStroe.refrashApplicationList()
        XCTAssert(appStroe.isHaveMoreData == true)
        XCTAssert(appStroe.applicationList.count == 10)
    }

}

